-- calculate percentage contribution of each pizza type in revenue.
select pizza_types.category, round((sum(order_details.quantity*pizzas.price)/ (select sum(order_details.quantity*pizzas.price) as total_sales from order_details join pizzas on order_details.pizza_id = pizzas.pizza_id))*100,2) as revenue from pizza_types join pizzas on pizzas.pizza_type_id = pizza_types.pizza_type_id join order_details on order_details.pizza_id = pizzas.pizza_id group by pizza_types.category order by revenue desc limit 3;